// CREATE TABLE `barbeariadocareca`.`abc` (`id` INT(11) NOT NULL AUTO_INCREMENT , `nome_usuario` VARCHAR(128) NOT NULL , `email` VARCHAR(64) NOT NULL , `data_de_nascimento` DATE NOT NULL , `nome_banco` VARCHAR(64) NOT NULL , `numero_agencia` VARCHAR(5) NOT NULL , `numero_conta` VARCHAR(15) NOT NULL , `chave_pix` VARCHAR(64) NOT NULL , `data_criacao` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;